const transporter = (authUser , authPassword) => {
    return nodemailer.createTransport({
        service: "smtp",
        host: process.env.EMAIL_HOST,
        name: process.env.EMAIL_NAME,
        port: process.env.EMAIL_PORT,
        secure: true,
        auth: {
            user: authUser,
            pass: authPassword
        }
    });
};

